mapper.select:
SELECT

FROM achieve_config a

WHERE a.id = #{id}

SELECT

FROM achieve_config a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM achieve_config a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE achieve_config SET 	
	name = #{name},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE achieve_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
